import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Todo {
  id: number;
  title: string;
  status: string;
}

const TodoList: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [newTodo, setNewTodo] = useState<string>('');

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      const response = await axios.get<Todo[]>('http://localhost:3000/todo');
      setTodos(response.data);
    } catch (error) {
      console.error('Error fetching todos:', error);
    }
  };

  const addTodo = async () => {
    try {
      await axios.post('http://localhost:3000/todo', {
        title: newTodo,
        status: 'pending',
      });
      setNewTodo('');
      fetchTodos();
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  const deleteTodo = async (id: number) => {
    try {
      await axios.delete(`http://localhost:3000/todo/${id}`);
      fetchTodos();
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const updateTodoStatus = async (todoId: number, newStatus: string) => {
    try {
      await axios.patch(`http://localhost:3000/todo/${todoId}/status`, { status: newStatus });
      fetchTodos();
    } catch (error) {
      console.error('Error updating todo status:', error);
    }
  };

  return (
    <div>
      <h1>Todo List</h1>
      <input
        type="text"
        placeholder="Enter new todo"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
      />
      <button onClick={addTodo}>Add Todo</button>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            {todo.title} - {todo.status}
            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
            <button onClick={() => updateTodoStatus(todo.id, 'completed')}>Mark as Completed</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
