// TodoList.tsx

import React, { useState, useEffect } from 'react';
import { fetchTodos, addTodo, deleteTodo, updateTodoStatus } from '../action/todo'; // Імпорт функцій дій з todo
import './todo.module.css'; 

interface Todo {
  id: number;
  title: string;
  status: string;
}

const TodoList: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [newTodo, setNewTodo] = useState<string>('');

  useEffect(() => {
    fetchTodoList();
  }, []);

  const fetchTodoList = async () => {
    try {
      const todoList = await fetchTodos();
      setTodos(todoList);
    } catch (error) {
      console.error('Error fetching todos:', error);
    }
  };

  const handleAddTodo = async () => {
    try {
      await addTodo(newTodo);
      setNewTodo('');
      fetchTodoList();
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  const handleDeleteTodo = async (id: number) => {
    try {
      await deleteTodo(id);
      fetchTodoList();
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const handleUpdateTodoStatus = async (todoId: number, newStatus: string) => {
    try {
      await updateTodoStatus(todoId, newStatus);
      fetchTodoList();
    } catch (error) {
      console.error('Error updating todo status:', error);
    }
  };

  return (
    <div className="todo-container">
      <h1>Todo List</h1>
      <input
        type="text"
        className="todo-input"
        placeholder="Enter new todo"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
      />
      <button className="add-todo-button" onClick={handleAddTodo} disabled={!newTodo.trim()}>Add Todo</button>
      <ul className="todo-list">
        {todos.map((todo) => (
          <li key={todo.id} className="todo-item">
            <span className="todo-item-content">{todo.title} - {todo.status}</span>
            <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
            {todo.status === 'pending' && (
              <button onClick={() => handleUpdateTodoStatus(todo.id, 'in_progress')}>Start</button>
            )}
            {todo.status === 'in_progress' && (
              <button onClick={() => handleUpdateTodoStatus(todo.id, 'completed')}>Complete</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
